import React from "react";

export default class StatefulGreeting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // date: new Date(),
      // nameColor: "red"
      count: 0
    };
  }

  render() {
    return (
      <div className="greeting">
        <h1>
          Hello, {this.props.name}, I'm a stateful component!
        </h1>
      </div>
    );
  }
}
